document.addEventListener('DOMContentLoaded',()=>{const btn = document.getElementById('menuBtn');const html = document.documentElement;console.log('[nav] script loaded');if(!btn){console.warn('[nav] menuBtn not found');return;}btn.addEventListener('click',()=>{const isOpen = html.classList.toggle('nav-open');btn.setAttribute('aria-expanded',isOpen ? 'true':'false');console.log('[nav] toggled',isOpen);});});